import React, { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimes } from '@fortawesome/free-solid-svg-icons';
import { Li, Crug, Crug1, InputСircle} from "./styles/style";
export default function Todo({todo, completedTask, deleteTodo, i}) {
  const [mouseOver, setMouseOver] = useState(false)
  return (
    <Li 
    onMouseEnter={()=>setMouseOver(true)}
    onMouseLeave={()=>setMouseOver(false)}
    >
            <InputСircle>
            if (i == 1){
              <Crug onClick={()=>completedTask(todo.id)}></Crug>
            } else {
<Crug1 onClick={()=>completedTask(todo.id)}></Crug1>
            }

              <div key={todo.id}>
                    {todo.text}
               </div>
               </InputСircle>
               {mouseOver && (
                <FontAwesomeIcon icon={faTimes} onClick={()=>deleteTodo(todo.id)}/> 
               )}
    </Li>
  )
}
