import React, { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimes } from '@fortawesome/free-solid-svg-icons';
export default function Todo({todo, completedTask, deleteTodo}) {
  return (
    <div style={{marginLeft: "300px", backgroundColor: "#9e9b9b", maxWidth: "150px"}}>

            <label style={{display: "flex", border: "#000000 solid 2px"}}>
                <input type="checkbox" onClick={()=>completedTask(todo.id)}/>
                <p key={todo.id}>
                    {todo.text}
                    <FontAwesomeIcon icon={faTimes} style={{marginLeft: "80px"}}/> 
                </p>
            </label>

    </div>
  )
}
