import { useState } from "react";
import Todos from "./Todos";
import Footer from "./Footer";
import uuid from 'react-uuid';
export default function Inputform() {
 const [todo, setTodo] = useState('')
 const [filter, setFilter] = useState('all')
 const [todos, setTodos] = useState([])
 const [completed, setCompleted] = useState(false)
 function completedTask(id) {
 const completedTodos = todos.map(todo => {

    if (id === todo.id) {
      return {...todo, completed: !todo.completed}
    }
    return todo;
  })

  setTodos(completedTodos)

}



function deleteTodo(id) {
  const completedTodos = todos.filter(todo => todo.id != id)
  setTodos(completedTodos)
}



 function handleSendInput(e){
  e.preventDefault()

  setTodos(
    [...todos, {id: uuid(), text: todo, completed: completed}]
  )
  setTodo('')
}

function handleActive(){
  const completedTodos = todos.filter(todo => todo.completed === false)
  setTodos(completedTodos) 
}

function handleCompleted(){
  const completedTodos = todos.filter(todo => todo.completed === true)
  setTodos(completedTodos)
}

function getFilteredTodos(filter) {
  const filteredTodos = [];
  switch (filter){
    case 'all':
      filteredTodos = [...todos]
      break
    case 'completed':
      filteredTodos = todos.filter(todo => todo.completed === true)
      break
    case 'activ':
      filteredTodos = todos.filter(todo => todo.completed === false)
      break
  }
  return filteredTodos
}



  return (
    <>
      <form onSubmit={handleSendInput} style={{marginLeft: "300px", marginTop: "120px"}}>
      <input
        type="text"
        placeholder="What needs to be done?"
        value={todo}
        onChange={e=>setTodo(e.target.value)}
      />
    </form>
    <Todos todos={ getFilteredTodos } completedTask={completedTask} deleteTodo={deleteTodo} />

    <Footer todos={ todos } handleActive={handleActive} handleCompleted={handleCompleted}/>
    </>

  );
}
