import { useState } from "react";
import Todos from "./Todos";
import Footer from "./Footer";
import uuid from 'react-uuid';
import { Title, Container, Input } from "./styles/style";
import GlobalStyles from './styles/reset'
export default function Inputform() {
 const [todo, setTodo] = useState('')
 const [filter, setFilter] = useState('all')
 const [todos, setTodos] = useState([])
 const [isActive, setIsActive] = useState(false);
 const [completed, setCompleted] = useState(false)
 const [id, setI] = useState(1)


 function completedTask(id) {
 const completedTodos = todos.map(todo => {
    if (id === todo.id) {
      return {...todo, completed: !todo.completed}
    }
    return todo;
  })
  setTodos(completedTodos)
  setI(2)
}

function deleteTodo(id) {
  const completedTodos = todos.filter(todo => todo.id != id)
  setTodos(completedTodos)
}

 function handleSendInput(e){
  e.preventDefault()
  setTodos(
    [...todos, {id: uuid(), text: todo, completed: completed}]
  )
  setTodo('')
}

function changeFinter(newFilter) {
  setIsActive(!isActive); 
  setFilter(newFilter);

}

  return (
    <>
    <GlobalStyles />
    <Container>
      <Title>todos</Title>
      <form onSubmit={handleSendInput}>
      <Input
        type="text"
        placeholder="What needs to be done?"
        value={todo}
        onChange={e=>setTodo(e.target.value)}
        autofocus
      />
    </form>
    <Todos todos={ todos } filter={filter} completedTask={completedTask} deleteTodo={deleteTodo} />
    <Footer todos={ todos } getFilteredTodos={changeFinter} isActive={isActive} />
    </Container>
    </>
  );
}
