import React from 'react'
import { FooterSection, FooterButton } from "./styles/style";
export default function Footer({ todos, getFilteredTodos, isActive  }) {

  return (
    <FooterSection>
    <div> {todos.length} item left</div>
    <FooterButton isActive={isActive} >
    <button onClick={()=>getFilteredTodos('all')}>All</button>
    <button onClick={()=>getFilteredTodos('active')}>Active</button>
    <button onClick={()=>getFilteredTodos('completed')}>Completed</button>
    </FooterButton>
    </FooterSection>
  )
}
