import React, { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimes } from '@fortawesome/free-solid-svg-icons';
import { Li, Crug, InputСircle} from "./styles/style";
export default function Todo({todo, completedTask, deleteTodo}) {
  const [mouseOver, setMouseOver] = useState(false)
  const [isCompleted, setIsCompleted] = useState(todo.completed); 
console.log(isCompleted)
  return (
    <Li 
    onMouseEnter={()=>setMouseOver(true)}
    onMouseLeave={()=>setMouseOver(false)}
    >

            <InputСircle>
              <Crug onClick={()=>completedTask(todo.id)}  style={{ backgroundColor: isCompleted ? 'green' : 'transparent' }}></Crug>
              <div key={todo.id}>
                    {todo.text}
               </div>
               </InputСircle>
               {mouseOver && (
                <FontAwesomeIcon icon={faTimes} onClick={()=>deleteTodo(todo.id)}/> 
               )}




    </Li>
  )
}
