import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimes } from '@fortawesome/free-solid-svg-icons';
import { Li, Crug, InputСircle } from "./styles/style";

export default function Todo({ todo, completedTask, deleteTodo }) {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <Li 
      onMouseEnter={() => setIsHovered(true)} 
      onMouseLeave={() => setIsHovered(false)}
    >


      {/* Условная отрисовка иконки крестика */}
      {isHovered && (
        <FontAwesomeIcon 
          icon={faTimes} 
          onClick={() => deleteTodo(todo.id)} 
          style={{ cursor: 'pointer' }} // Добавляем курсор указателя для улучшения UX
        />
      )}
    </Li>
  );
}
